/**
    Get a serialized string representation of a variable using JSON encoding.
    @return Returns a string variable or null if an exception is thrown.
 */
EjsString *ejsToJSON(Ejs *ejs, EjsObj *vp, EjsObj *options)
{
    EjsFunction     *fn;
    EjsString       *result;
    EjsObj          *argv[1];
    EjsName         qname;
    int             argc;

    if (vp == 0) {
        return ejsCreateString(ejs, "undefined");
    }
#if UNUSED
    if (vp->jsonVisited) {
        return ejsCreateString(ejs, "this");
    }    
    vp->jsonVisited = 1;
#endif
    
    fn = (EjsFunction*) ejsGetPropertyByName(ejs, (EjsObj*) vp->type->prototype, ejsName(&qname, NULL, "toJSON"));
    if (fn == 0) {
        fn = (EjsFunction*) ejsGetPropertyByName(ejs, (EjsObj*) ejs->objectType->prototype, &qname);        
    }
    if (ejsIsFunction(fn)) {
        if (options) {
            argc = 1;
            argv[0] = options;
        } else {
            argc = 0;
            argv[0] = NULL;
        }
        result = (EjsString*) ejsRunFunction(ejs, fn, vp, argc, argv);
    } else {
        result = ejsToString(ejs, vp);
    }
#if UNUSED
    vp->jsonVisited = 0;
#endif
    return result;
}


/*
    Fully construct a new object. We create a new instance and call all required constructors.
 */
EjsObj *ejsCreateInstance(Ejs *ejs, EjsType *type, int argc, EjsObj **argv)
{
    EjsObj      *obj;

    mprAssert(type);

    obj = ejsCreate(ejs, type, 0);
    if (obj == 0) {
        ejsThrowMemoryError(ejs);
        return 0;
    }
    if (type->constructor.block.obj.isFunction) {
        obj->permanent = 1;
        ejsRunFunction(ejs, (EjsFunction*) type, obj, argc, argv);
        obj->permanent = 0;
    }
    return obj;
}



int _ejsIs(EjsObj *obj, int slot)
{
    EjsType     *tp;

    if (obj == 0) {
        return 0;
    }
    if (obj->type->id == slot) {
        return 1;
    }
    for (tp = ((EjsObj*) obj)->type->baseType; tp; tp = tp->baseType) {
        if (tp->id == slot) {
            return 1;
        }
    }
    return 0;
}


